import React, { useState } from 'react';
import axios from 'axios';

export default function HotDogPage() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [result, setResult] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setPreviewUrl(URL.createObjectURL(file));
    setResult(null);
  };

  const handleDetectClick = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('image', selectedFile);

    const res = await axios.post('http://localhost:3002/api/detect', formData);
    setResult(res.data.result);
  };

  const predefinedImages = [
    { label: "Hot Dog", src: "/images/hotdog.jpg" },
    { label: "Banana", src: "/images/banana.jpg" },
    { label: "Car", src: "/images/car.jpg" },
    { label: "Elephant", src: "/images/elephant.jpg" },
  ];

  const handlePredefinedClick = (imagePath) => {
    fetch(imagePath).then(res => res.blob()).then(blob => {
      const file = new File([blob], imagePath.split('/').pop(), { type: blob.type });
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(blob));
      setResult(null);
    });
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-red-600 text-white">
      <h1 className="text-4xl font-bold mb-6">HotDog or NotHotDog</h1>

      <input type="file" onChange={handleFileChange} className="mb-4" />

      <div className="mb-4 relative inline-block">
        {previewUrl && (
          <>
            <img 
              src={previewUrl} 
              alt="Preview" 
              className="max-w-[300px] rounded shadow-lg"
            />
            {result === "Hot Dog" && (
              <img
                src="/images/checkmark.gif"
                alt="Hot Dog!"
                className="absolute inset-0 m-auto max-w-[150px] z-10 pointer-events-none"
              />
            )}
          </>
        )}
      </div>

      <button 
        onClick={handleDetectClick} 
        className="bg-green-500 text-white py-2 px-6 rounded shadow hover:bg-green-600 transition"
      >
        Detect
      </button>

      {result && (
        <div className="text-3xl font-bold mt-4">
          {result === "Hot Dog" ? (
            <span className="text-green-300">✔️ Hot Dog!!!</span>
          ) : (
            <span className="text-red-200">❌ Not Hot Dog!</span>
          )}
        </div>
      )}

      <div className="flex justify-center gap-4 mt-8">
        {predefinedImages.map(img => (
          <img 
            key={img.label} 
            src={img.src} 
            alt={img.label} 
            className="w-[100px] h-[100px] object-cover rounded cursor-pointer shadow"
            onClick={() => handlePredefinedClick(img.src)} 
          />
        ))}
      </div>
    </div>
  );
}
